.btn-group {
    position: relative;
    font-size: 0;
    white-space: nowrap;
    vertical-align: middle;
}

.user-info {
  text-align: right;
  padding: 24px 12px 0 0;
  font-size: 0.9em;
  text-shadow: 0 1px 0 #FFF;
}
.user-info a {
  text-decoration: underline;
}

.icon-ok:before {
  content: "\f00c";
}
.icon-search:before {
  content: "\f002";
}
.icon-cog:before {
  content: "\f013";
}
.icon-resize-full:before {
  content: "\f065";
}
.search-list .close-btn {
  height: 16px;
  width: 16px;
  position: absolute;
  top: 10px;
  right: 11px;
  opacity: 0.8;
  background: url(../images/close-icon.png) no-repeat left top;
  text-indent: -999em;
}

.table-container {
  overflow: auto;
}
