  display: none;
  padding-top: 10px;
  position: absolute;
  min-width: 150px;
  left: 0;
  top: 22px;
}
.navigation ul ul .submenu {
  padding: 0 0 0 10px;
  left: 97%;
  top: 1px;
}
.navigation ul li:hover > .submenu {
  display: block !important;
}

.manage-nav {
  font-size: 0.9em;
  margin: -5px 0 10px;
  font-weight: bold;
}
.manage-nav ul {
  margin: 0;
  padding-left: 5px;
  list-style: none;
  border-bottom: 1px solid #d6d6d6;
}
.manage-nav li {
  float: left;
  margin: 10px 2px -1px;
  border: 1px solid #d6d6d6;
  border-radius: 3px 3px 0 0;
  background: #DDD;
