  /* IE10+ */
  background: linear-gradient(to bottom, #d9d9d9 0%, #ffffff 100%);
  /* W3C */
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#d9d9d9', endColorstr='#ffffff', GradientType=0);
  /* IE6-9 */
}
.manage-nav a {
  display: block;
  color: #2c2c2c;
  padding: 8px 14px 6px;
  text-shadow: 0 1px 0 #FFF;
}
.manage-nav a:hover {
  text-decoration: none;
}

.rnr-tabs li.selected {
  background: #307579;
}
.rnr-tabs li.has-error a {
  padding-left: 33px;
  background-image: url("../images/tab-error.png");
  background-position: 9px center;
  background-repeat: no-repeat;
}

.row-fluid {
    width: 100%;
}

.dropdown-menu li > a {
    padding: 6px 0 6px 13px;
    color: #333;
    text-decoration: none;
    display: block;
    clear: both;
    font-weight: normal;
    line-height: 18px;
    white-space: nowrap;
}

.dropdown-menu li > a:hover, .dropdown-menu li > a:focus, .dropdown-submenu:hover > a {
    color: #ffffff;
    text-decoration: none;
    background-color: #0088cc;
    background-color: #0081c2;
    background-image: -moz-linear-gradient(top, #0088cc, #0077b3);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#0088cc), to(#0077b3));
    background-image: -webkit-linear-gradient(top, #0088cc, #0077b3);
    background-image: -o-linear-gradient(top, #0088cc, #0077b3);
    background-image: linear-gradient(to bottom, #0088cc, #0077b3);
    background-repeat: repeat-x;
    filter: progid:dximagetransform.microsoft.gradient(startColorstr='#ff0088cc', endColorstr='#ff0077b3', GradientType=0);
}

.input-append, .input-prepend {
    margin-bottom: 5px;
    font-size: 0;
    white-space: nowrap;
}

input[rnr-validator="positiveInteger"] {
  text-align: right;
}

input[type="radio"] {
    margin: 0;
}

input[ui-date] {
    background: #ffffff url(../images/calendar-icon.png) no-repeat 98% center;
    cursor: pointer;
}

input[type="checkbox"] {
    margin: 4px 0 0;
    margin-top: 1px \9;
    line-height: normal;
    cursor: pointer;
}

select, input[type="text"] {
    display: inline-block;
    height: 20px;
    padding: 4px 6px;
    margin-bottom: 9px;
    font-size: 14px;
    line-height: 20px;
    color: #555;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}

